export function Base() {

    const {currentUserInfo, setCurrentUserInfo} = useContext(GlobalState);
    const updatedStates = OnConnect();
    console.log(updatedStates);

    return (
        <section>
            <div class="rt-container">
                <div class="col-rt-12">
                    <div class="Scriptcontent">
    		            <div class="pregame_background">
                            <div class="box">
                                <h3>Click "Connect" to connect to your MetaMask Wallet!</h3>
                                <button onClick={() => {console.log(updatedStates);setCurrentUserInfo(updatedStates)}}>Connect</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}
