import { useState, useEffect } from "react";

export const [balanceInfo, setBalanceInfo] = useState({
  address: "-",
  balance: "-"
});

export const [currentProvider, setcurrentProvider] = useState({
  provider: "-"
});

export const [currentUser, setCurrentUser] = useState({
  signer: "-",
  signerAddress: "-"
});
