import { useMetaMask } from "metamask-react"

export function MMVerify() {
    const { status, connect, account, chainId, ethereum } = useMetaMask();

    if (status === "initializing") return (
        <div class="box">
            <h3>Please wait while we connect you to the MetaMask Servers...</h3>
        </div>
    )

    if (status === "unavailable") return (
        <div class="box">
            <h3>Unfortunately, MetaMask is currently unavailable. Please try again later, or click "Connect" to retry.</h3>
            <button onClick={connect}>Connect</button>
        </div>
    )

    if (status === "notConnected") return (
        <div class="box">
            <h3>Click "Connect" to connect to your MetaMask Wallet!</h3>
            <button onClick={connect}>Connect</button>
        </div>
    )

    if (status === "connecting") return (
        <div class="box">
            <h3>Connecting...</h3>
        </div>
    )
    if (status === "connected") return "connected"

    return null;
}
