import { useState, useEffect, useContext } from "react";
import { ethers } from "ethers";
import { GlobalState } from "./global_state"

export function updatedStates() {
    const currProvider = new ethers.providers.Web3Provider(window.ethereum, "any");
    currProvInfo = currProvider.send("eth_requestAccounts", []);
    const signer = currProvInfo.getSigner();
    const signerAddress = signer.getAddress();

    origStates = GlobalStates;
    updatedStates = {
        provider: currProvider,
        signer: signer,
        signerAddress: signerAddress,
        balance: "-"
    }
    

    
   setCurrentUserInfo(updatedStates)
    

}

export const OnConnect = async () => {
    const currProvider = new ethers.providers.Web3Provider(window.ethereum, "any");
    await currProvider.send("eth_requestAccounts", []);
    const signer = await currProvider.getSigner();
    const signerAddress = await signer.getAddress();

    origStates = useContext(GlobalState);
    updatedStates = {
        provider: currProvider,
        signer: signer,
        signerAddress: signerAddress,
        balance: "-"
    }

    
    origStates.setCurrentUserInfo(updatedStates)
    

  };
