import { useState, useEffect } from "react";
import { useContext } from "react/cjs/react.production.min";
import { updatedStates } from "./updated_states";

const GlobalStateInitial = React.createContext();
//const GlobalStateUpdate = React.createContext();

 export function GlobalStates() {
   return useContext(GlobalStateInitial);
 }

export function GlobalStatesProvider({ children })  {
  
  const [currentUserInfo, setCurrentUserInfo] = useState({
    provider: "-",
    signer: "-",
    signerAddress: "-",
    balance: "-"
  });

  return (
    <GlobalStateInitial.Provider value={currentUserInfo}>
      <updatedStates>{children}</updatedStates>
    </GlobalStateInitial.Provider>
    )

}
