import { useState, useEffect } from "react";

export const [balanceInfo, setBalanceInfo] = useState({
  address: "-",
  balance: "-"
});

export const [currentProvider, setcurrentProvider] = useState({
  provider: "-"
});

export const [currentUser, setCurrentUser] = useState({
  signer: "-",
  signerAddress: "-"
});

export function stateDeclare() {
  const [balanceInfo, setBalanceInfo] = useState({
    address: "-",
    balance: "-"
  });
  
  const [currentProvider, setcurrentProvider] = useState({
    provider: "-"
  });
  
  const [currentUser, setCurrentUser] = useState({
    signer: "-",
    signerAddress: "-"
  });
}
