import { useState, useEffect } from "react";
import { ethers } from "ethers";
import erc20abi from "../ERC20abi.json";
import { currentProvider } from "./states";

export const Erc20 = new ethers.Contract(
    "contract address goes here",
    erc20abi,
    currentProvider
);
