import { updateCurrentUser } from "./functions/curr_user"
import { Connect } from "./updated_states"
import { ethers } from "ethers";
import { GlobalState } from "./global_state"
import { useContext } from "react";



export function Base() {

    function Connect() {
        const {currentUserInfo, setCurrentUserInfo} = useContext(GlobalState);
        const OnConnect = async () => {
            const currProvider = new ethers.providers.Web3Provider(window.ethereum, "any");
            await currProvider.send("eth_requestAccounts", []);
            const signer = currProvider.getSigner();
            const signerAddress = await signer.getAddress();
            return ({
                provider: currProvider,
                signer: signer,
                signerAddress: signerAddress,
                balance: "-"
            })
        }
        
        
        return setCurrentUserInfo(OnConnect);
    
    }

    return (
        <section>
            <div class="rt-container">
                <div class="col-rt-12">
                    <div class="Scriptcontent">
    		            <div class="pregame_background">
                            <div class="box">
                                <h3>Click "Connect" to connect to your MetaMask Wallet!</h3>
                                <button onClick={Connect}>Connect</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}
