import { useState, useEffect } from "react";
import { ethers } from "ethers";
import erc20abi from "./ERC20abi.json";
import { updateCurrentUser } from "./components/functions/curr_user";
import { StartPage } from "./components/StartPage";
import { currentProvider, setcurrentProvider, currentUser, setCurrentUser } from "./components/states";
import { updatedStates } from "./components/updated_states";
import { GlobalStatesProvider } from "./components/states";

export function MyApp() {

    useEffect(() => {
        setcurrentProvider(updateCurrentUser[0]);
    }, [currentProvider.provider]);

    useEffect(() => {
        setCurrentUser(updateCurrentUser[1]);
    }, [currentUser.signer , currentUser.signerAddress]);

    return (
        <GlobalStatesProvider>
            <StartPage />
        </GlobalStatesProvider>
    )

}
