import React from "react"
import { useState, useEffect, createContext } from "react";
import { useContext, useMemo } from "react";
import { GlobalState } from "./global_state";
import { Pages } from "./site_stuff/pages";
import { OnConnect } from "./updated_states";
import { AccChange } from "./accChange";

//  export function GlobalStates() {
//    return useContext(GlobalState);
//  }


export function GlobalStateDefault({ children })  {
  
  const [currentUserInfo, setCurrentUserInfo] = useState({
    provider: "-",
    signer: "-",
    signerAddress: "-",
    balance: "-"
  });

  useEffect(() => {
    if (currentUserInfo.signerAddress !== "-") {
      setCurrentUserInfo(OnConnect());
    }
  }, [ currentUserInfo.signerAddress, currentUserInfo.provider, currentUserInfo.balance]);

  const globalStateValue = useMemo(() => ({ currentUserInfo, setCurrentUserInfo}), [ currentUserInfo.signerAddress, currentUserInfo.provider, currentUserInfo.balance])
  // console.log(currentUserInfo)
  console.log(globalStateValue)

  return (
    <GlobalState.Provider value={globalStateValue}>
      {children}
    </GlobalState.Provider>
    )

}
