import { OnConnect } from "./updated_states"
import { ethers } from "ethers";
import { GlobalState } from "./global_states/global_state"
import { useContext } from "react";
import { Connect } from "./accChange";



export function Base() {

   const { currentUserInfo, setCurrentUserInfo } = useContext(GlobalState);

    return (
        <section>
            <div class="rt-container">
                <div class="col-rt-12">
                    <div class="Scriptcontent">
    		            <div class="pregame_background">
                            <div class="box">
                                <h3>Click "Connect" to connect to your MetaMask Wallet!</h3>
                                <button onClick={() => setCurrentUserInfo(prev => {return { ...prev, user: Connect }})}>Connect</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}
