export const [currentUserInfo, setCurrentUserInfo] = useState(() => {
    return {
        provider: "-",
        signer: "-",
        signerAddress: "-",
        balance: "-",
        game: {
            deck: [],
            dealer: null,
            player: null,
            wallet: 0,
            inputValue: '',
            currentBet: null,
            gameOver: false,
            message: null 
        }
    }
  });
