import { useContext, useEffect } from "react"
import { GlobalState } from "../../global_states/global_state"
import { WinningCard, JackpotCard, GuessCard, BlankCard } from "./createCards";



export function Cards() {
    const { currentUserInfo, setCurrentUserInfo, currentGameInfo, setGameInfo, accReqPending, setAccReqPending} = useContext(GlobalState);
    const level = currentGameInfo.level;
    const gameOver = currentGameInfo.gameOver;
    var cardsArr = [];
    useEffect(() => {

    }, [currentGameInfo.gameOver])

    if (!gameOver) {
        for (let i = 0; i < level*3; i++) {
            cardsArr.push(GuessCard(i, checkCard))
        }
    }

    function checkCard(e) {
        console.log("checkCard just ran");
        console.log(e);
        console.log(e.target.id);
        setGameInfo(prev => {
            return {
                ...prev,
                gameOver: true
            }
        })
        let winner = Math.floor(Math.random() * level*3);
        cardsArr = [];
        for (let j = 0; j < level * 3; j++) {
            if (e.target.id == winner) {
                cardsArr.push(WinningCard())
            } else {
                cardsArr.push(BlankCard())
            }
        }
        
    }

    console.log(cardsArr);


    return (
        <>
            <h3>Choose the right card to move onto the next level!</h3>
            <table className="cards">
                <tr>
                    {cardsArr}
                </tr>
            </table>
        </>
    )
}
